import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import "./style.css";
import { Link } from "react-router-dom";
import pro from "./img/Bootlabs-logo.gif";
import lap from './img/lap.png'
import { ReactComponent as IconSearch } from "bootstrap-icons/icons/search.svg";
import { ReactComponent as IconCart3 } from "bootstrap-icons/icons/cart3.svg";
import { useNavigate } from 'react-router-dom';

export default function Product()
{
    const history = useNavigate();
    const [showSearchResults, setShowSearchResults] = useState(false);
    const [data,setData] = useState([])
    const [search, setSearch] = useState("")
    const [searchdata, setSearchdata] = useState("")

    useEffect(()=>{
        axios.get(`http://127.0.0.1:8000/users/productview`)
            .then((response) => {
                console.log("response",response )
                console.log("pd",response.data[0].P )
                setData(response.data)
            });

    },[])
    function sign()
    {
        const cookies = document.cookie.split(";").map((cookie) => cookie.trim());
        return cookies.some((cookie) => cookie.startsWith(`_id`));
    }
    function logout()
    {
        document.cookie = `_id=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;
        alert("Successfully logged out ")
    }
    // document.cookie = `_id=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/;`;

    function addToCart(id)
    {
        if(sign()){
            console.log(id);
        }
        else{
            alert("Please log in");
            history('/signin');
        }
    }

    const searchData = (e) =>
    {
        e.preventDefault();
        let errors = {};
        if (Object.keys(errors).length === 0) {
            axios.get(`http://127.0.0.1:8000/users/search?name=${search}`)
                .then(data => {
                    console.log(data.statusText);
                    if(data.statusText==="OK"){
                        setSearchdata(data.data)
                        setShowSearchResults(true);
                        // setResponse(data.data);
                        // history('/');
                    }
                    else{
                        alert("Please check your credentials");
                    }
                })
        }
    }



    return (
        <React.Fragment>
            <header className="p-3 border-bottom bg-light">
                <div className="container-fluid">
                    <div className="row g-3">
                        <div className="col-md-3 text-center">

                            <img className="img" src={pro} ></img>

                        </div>
                        <div className="col-md-5">

                            <form onSubmit={searchData} className="search">
                                <div className="input-group">
                                    <input
                                        id="search"
                                        value={search}
                                        onChange={(e) => setSearch(e.target.value)} required
                                        name="search"
                                        type="text"
                                        className="form-control"
                                        placeholder="Search"
                                        required
                                    />
                                    <label className="visually-hidden" htmlFor="search"></label>
                                    <button
                                        className="btn btn-primary text-white"
                                        type="submit"
                                        aria-label="Search"
                                    >
                                        <IconSearch />
                                    </button>
                                </div>
                            </form>
                            {/*<Search responseData={response} />*/}
                        </div>
                        <div className="col-md-4">

                            <div className="position-relative d-inline me-3">
                                <Link to="/cart" className="btn btn-primary">
                                    <IconCart3 className="i-va" />
                                    <div className="position-absolute top-0 start-100 translate-middle badge bg-danger rounded-circle">
                                        2
                                    </div>
                                </Link>
                            </div>&emsp;
                            {
                                (
                                    sign()?(
                                            <>
                                                <div class="dropdown">
                                                    <button class="dropbtn">My Account</button>
                                                    <div className="dropdown-content">
                                                        <Link to="/">My Profile</Link>
                                                        <Link to="/">View Cart</Link>
                                                        <Link to="/">Orders</Link>
                                                        <Link to="/">Notification</Link>
                                                        <a href="/" onClick={logout}>Log out</a>
                                                    </div>
                                                </div>
                                            </>
                                        ):
                                        (
                                            <>
                                                <div className="btn-group">
                                                    <button className="buttons"><Link to="/signin">Sign In</Link></button>
                                                </div>
                                            </>
                                        )
                                )
                            }

                        </div>
                    </div>
                </div>
            </header>
            <div className="p-3 border-bottom bg-light">
                <div className="rows">
                    <div className="col-md-2 text-center">
                        <Link to="/products"><p className="links">All Products</p></Link>
                    </div>
                    <div className="col-md-2 text-center">
                        <Link to="/laptop"><p>Laptop</p></Link>
                    </div>
                    <div className="col-md-2 text-center">
                        <Link to="/mobile"><p>Mobile</p></Link>
                    </div>
                    <div className="col-md-2 text-center">
                        <Link to="/homeappliances"><p>Home Appliances</p></Link>
                    </div>
                    <div className="col-md-2 text-center">
                        <Link to="/fashion"><p>Fashion</p></Link>
                    </div>
                    <div className="col-md-2 text-center">
                        <Link to="/"><p>Customer Service</p></Link>
                    </div>
                </div>
            </div>
            <br/><br/>
            <div className="row-s">
                {showSearchResults ? (
                        <div className="row-s">
                            {searchdata.map((data)=>
                            (
                            <div className="column-s">
                                <div className="card">
                                    <img className="cardimage" src={lap}/>
                                    <p className="ps">{data.oneline}</p>
                                    <p className="ps-star">
                                        {data.rating === 1 ? (
                                            <>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star"></span>
                                                <span className="fa fa-star"></span>
                                                <span className="fa fa-star"></span>
                                                <span className="fa fa-star"></span>
                                            </>
                                        ) : data.rating === 2 ? (
                                            <>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star"></span>
                                                <span className="fa fa-star"></span>
                                                <span className="fa fa-star"></span>

                                            </>
                                        ) : data.rating === 3 ? (
                                            <>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star"></span>
                                                <span className="fa fa-star"></span>
                                            </>
                                        ) : data.rating === 4 ? (
                                            <>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star"></span>
                                            </>
                                        ) : data.rating === 5 ? (
                                            <>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                                <span className="fa fa-star checked"></span>
                                            </>
                                        ) : null}

                                        <span className="ps-s">price : <span className="spans">&#8377;</span>{ data._id }</span></p>
                                    {/*<p className="ps-ss">price : <span className="spans">&#8377;</span>{ data.price }</p>*/}
                                    <div className="row">
                                        <div className="column">
                                            <button className="cart-1" onClick={() => addToCart(data._id)}>Add to cart</button>
                                            <button  className="view-1" >
                                                {/*<Link to="/view">*/}
                                                View
                                                {/*</Link>*/}
                                            </button>

                                        </div>
                                    </div>

                                </div>
                            </div>
                            ))}
                        </div>
                    ):
                    data.map((data)=>
                    (
                        <div className="column-s">
                            <div className="card">
                                <img className="cardimage" src={lap}/>
                                <p className="ps">{data.oneline}</p>
                                <p className="ps-star">
                                    {data.rating === 1 ? (
                                        <>
                                        <span className="fa fa-star checked"></span>
                                        <span className="fa fa-star"></span>
                                        <span className="fa fa-star"></span>
                                        <span className="fa fa-star"></span>
                                        <span className="fa fa-star"></span>
                                        </>
                                    ) : data.rating === 2 ? (
                                        <>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star"></span>
                                            <span className="fa fa-star"></span>
                                            <span className="fa fa-star"></span>

                                        </>
                                    ) : data.rating === 3 ? (
                                        <>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star"></span>
                                            <span className="fa fa-star"></span>
                                        </>
                                    ) : data.rating === 4 ? (
                                        <>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star"></span>
                                        </>
                                    ) : data.rating === 5 ? (
                                        <>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                            <span className="fa fa-star checked"></span>
                                        </>
                                    ) : null}

                                    <span className="ps-s">price : <span className="spans">&#8377;</span>{ data.price }</span></p>
                                {/*<p className="ps-ss">price : <span className="spans">&#8377;</span>{ data.price }</p>*/}
                                <div className="row">
                                    <div className="column">
                                        <button className="cart-1" onClick={addToCart}>Add to cart</button>
                                        <button  className="view-1" >
                                            {/*<Link to="/view">*/}
                                                View
                                            {/*</Link>*/}
                                        </button>

                                </div>
                                </div>

                            </div>
                        </div>
                    ))}
            </div>

        </React.Fragment>

    );

}
