import axios from "axios";
import React from "react";
import { useEffect, useState } from "react";
import "./style.css";
import { Link } from "react-router-dom";
import pro from "./img/Bootlabs-logo.gif";
import dell from "./img/Dell.webp"
import lap from "./img/Laptops.webp"
import { ReactComponent as IconSearch } from "bootstrap-icons/icons/search.svg";
import { ReactComponent as IconCart3 } from "bootstrap-icons/icons/cart3.svg";
import {email} from "./extra/validate";
export default function Signin()
{
    const [fname, setFname] = useState("")
    const [lname, setLname] = useState("")
    const [mail, setMail] = useState("")
    const [phone, setPhone] = useState("")
    const [pass, setPass] = useState("")
    const [formErrors, setFormErrors] = useState({
        fname: '',lname:'',mail:'',phone:'',pass:'',
    });

    const handleSubmit = (e) =>
    {
        e.preventDefault();
        let errors = {};
        if (fname.length < 3) {
            errors.fname = 'First name must be at least 3 characters long.';
        }
        if (lname.length < 1) {
            errors.fname = 'Last name must be at least 1 character long.';
        }
        if (phone.length < 10) {
            errors.phone = 'Phone number must be 10 digit';
        }
        if (pass.length < 8) {
            errors.pass = 'Password must be at least 8 characters long';
        }
        setFormErrors(errors);
        if (Object.keys(errors).length === 0) {
            axios.post(`http://127.0.0.1:8000/users/signup` ,{ first_name:fname,last_name:lname,email:mail,phone:phone,password:pass})
                .then((data) => {
                    console.log(data.status);
                    if(data.status==201){

                    }
                })
            console.log(fname);
        }
    }
    return (
        <React.Fragment>
            <header className="p-3 border-bottom bg-light">
                <div className="container-fluid">
                    <div className="row g-3">
                        <div className="col-md-3 text-center">

                            <img className="img" src={pro} ></img>

                        </div>
                        <div className="col-md-5">

                            <form action="#" className="search">
                                <div className="input-group">
                                    <input
                                        id="search"
                                        name="search"
                                        type="text"
                                        className="form-control"
                                        placeholder="Search"
                                        required
                                    />
                                    <label className="visually-hidden" htmlFor="search"></label>
                                    <button
                                        className="btn btn-primary text-white"
                                        type="submit"
                                        aria-label="Search"
                                    >
                                        <IconSearch />
                                    </button>
                                </div>
                            </form>
                        </div>
                        <div className="col-md-4">

                            <div className="position-relative d-inline me-3">
                                <Link to="/cart" className="btn btn-primary">
                                    <IconCart3 className="i-va" />
                                    <div className="position-absolute top-0 start-100 translate-middle badge bg-danger rounded-circle">
                                        2
                                    </div>
                                </Link>
                            </div>&emsp;
                            <div className="btn-group">
                                <button className="buttons"><Link to="/signin">Sign In</Link></button>
                            </div>

                        </div>
                    </div>
                </div>
            </header>
            <div className="container my-3">
                <div className="row border">
                    <div className="col-md-6 bg-light bg-gradient p-3 d-none d-md-block">
                        <Link to="/">
                            <img
                                src={dell}
                                alt="..."
                                className="img-fluid"
                            />
                        </Link>
                        <Link to="/">
                            <img
                                src={lap}
                                alt="..."
                                className="img-fluid"
                            />
                        </Link>
                    </div>
                    <div className="col-md-6 p-3">
                        <form onSubmit={handleSubmit}>
                        <h4 className="text-center">Sign Up</h4>

                        <label className="labelss">First Name: </label> <input className="uplabeldata" type="text" value={fname} onChange={(e) => setFname(e.target.value)} required placeholder="Enter your First name"
                        // onChange={(e) => setName(e.target.value)} required
                    /><br/>
                        {formErrors.fname && <p className="error">{formErrors.fname}</p>}
                        <label className="labelss">Last Name: </label> <input className="uplabeldata" type="text" value={lname} onChange={(e) => setLname(e.target.value)} required placeholder="Enter your Last name"
                     /><br/>

                        <label className="labelss">Email Id: </label> <input className="uplabeldata" type="email" value={mail} onChange={(e) => setMail(e.target.value)} required placeholder="Mail Id"
                    /><br/>
                        <label className="labelss">Mobile No: </label> <input className="uplabeldata" type="number" value={phone} onChange={(e) => setPhone(e.target.value)} required placeholder="Mobile no"
                    /><br/>
                            {formErrors.phone && <p className="error">{formErrors.phone}</p>}
                        <label className="labelss">Create Password: </label><input className="uplabeldata" type="password" value={pass} onChange={(e) => setPass(e.target.value)} required placeholder="********"
                    />
                            {formErrors.pass && <p className="error">{formErrors.pass}</p>}
                        <button type="submit" className="signinbutton">
                            {/*<Link className="sign" to="/">*/}
                                Create Account
                            {/*</Link>*/}
                        </button>
                        </form>
                    </div>
                </div>
            </div>
        </React.Fragment>

    );

}
